import React, { useState } from 'react'
import './App.scss'

const Calculator = () => {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');
  const [lastInput, setLastInput] = useState('');
  const [previousCalculation, setPreviousCalculation] = useState('');
  const [isActive, setIsActive] = useState(false);

  const keyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    const value = e.key

    if(value === '+' || value === '-' || value === '*' || value === '/' || value === '.' || value === '0' || +value) {
      if ((value === '+' && input.length === 0) || (value === '/' && input.length === 0) || (value === '*' && input.length === 0)) {
        return;
      }
      let firstChar = input[0];
      if (value !== '.' && firstChar === '0' && input.length === 1) {
        return;
      }
      if (value === '.' && input.includes('.')) {
        return;
      }
      let lastChar = input[input.length - 1];
      if ((value === '.' && lastChar === '.') || (value === '.' && lastChar === '+') || (value === '.' && lastChar === '-') || (value === '.' && lastChar === '/') || (value === '.' && lastChar === '*') ||
          (value === '+' && lastChar === '+') || (value === '+' && lastChar === '.') || (value === '+' && lastChar === '-') || (value === '+' && lastChar === '/') || (value === '+' && lastChar === '*') || 
          (value === '-' && lastChar === '-') || (value === '-' && lastChar === '.') || (value === '-' && lastChar === '+') || (value === '-' && lastChar === '/') || (value === '-' && lastChar === '*') ||
          (value === '/' && lastChar === '/') || (value === '/' && lastChar === '.') || (value === '/' && lastChar === '-') || (value === '/' && lastChar === '+') || (value === '/' && lastChar === '*') ||
          (value === '*' && lastChar === '*') || (value === '*' && lastChar === '.') || (value === '*' && lastChar === '-') || (value === '*' && lastChar === '+') || (value === '*' && lastChar === '/')){
        return;
      } 
      setInput(input + value);
    }

    if (value === 'Backspace') {
      setLastInput('CE');
      let equation = input;
      equation = equation.slice(0, -1);
      setInput(equation);
    }
    if (value === 'Enter') {
      let equation = input;
      let lastChar = equation; 
      // Replace all instances of x and รท with * and / respectively. This can be done using regex and the 'g' tag which will replace all instances of the matched character/substring
      equation = equation.replace(/x/g, '*').replace(/รท/g, '/');
      // Checking the last character of the equation. If it's an operator or a decimal, remove it
      if (lastChar === '+' || lastChar === '-' || lastChar === '*' || lastChar === '/' || lastChar === '.') {
        equation = equation.replace(/.$/, '');
      }
      if (equation) {
        setResult(eval(equation));
        // Check for the last input and set it to the result if it is "=" or "Enter"
        if (lastInput === '=' || lastInput === 'Enter') {
          setInput(result);
          setPreviousCalculation(result);
        } else {
          setInput('');
          setPreviousCalculation(input);
        }
      }
      return;
    }
  }

  const handleButtonPress = (e: React.MouseEvent<HTMLButtonElement>) => {
    setLastInput(e.currentTarget.name as string)

    if (e.currentTarget.name === '=') {
      let equation = input;
      let lastChar = equation[equation.length - 1]; 
      // Replace all instances of x and รท with * and / respectively. This can be done using regex and the 'g' tag which will replace all instances of the matched character/substring
      equation = equation.replace(/x/g, '*').replace(/รท/g, '/');
      // Checking the last character of the equation. If it's an operator or a decimal, remove it
      if (lastChar === '+' || lastChar === '-' || lastChar === '*' || lastChar === '/' || lastChar === '.') {
        equation = equation.replace(/.$/, '');
      }
      if (equation) {
        setResult(eval(equation));
        // Check for the last input and set it to the result if it is "=" or "Enter"
        if (lastInput === '=' || lastInput === 'Enter') {
          setInput(result);
          // Update the buttons with the result
          result.split('').forEach(num => {
            document.getElementById(num)?.click();
          })
        } else {
          setInput('');
        }
      }
      return;
    }

    if (e.currentTarget.name === 'CE') {
      let equation = input;
      equation = equation.slice(0, -1);
      setInput(equation);
    }
    if (e.currentTarget.name === 'DEL') {
      setInput('');
      setResult('');
    }
    let lastChar = input[input.length - 1];
    if (e.currentTarget.name === '.' && lastChar !== '.') {
      setInput(input + e.currentTarget.name);
    }
    if ((e.currentTarget.name === '+' && input.length === 0) || (e.currentTarget.name === '/' && input.length === 0) || (e.currentTarget.name === '*' && input.length === 0)) {
      return;
    }
    if (lastChar === '+' || lastChar === '-' || lastChar === '*' || lastChar === '/') {
      if (e.currentTarget.name === '+' || e.currentTarget.name === '-' || e.currentTarget.name === '*' || e.currentTarget.name === '/') {
        return;
      }
    }
    if (e.currentTarget.name !== 'DEL' && e.currentTarget.name !== 'CE') {
      setInput(input + e.currentTarget.name);
    }
  };

  return (
    <body>
      <svg viewBox="0 0 100 100" preserveAspectRatio="xMidYMid slice">
        <defs>
          <radialGradient id="Gradient1" cx="50%" cy="50%" fx="10%" fy="50%" r=".5">
            <animate attributeName="fx" dur="34s" values="0%;3%;0%" repeatCount="indefinite" />
            <stop offset="0%" stop-color="#000" />
            <stop offset="100%" stop-color="#000" />
          </radialGradient>
          <radialGradient id="Gradient2" cx="50%" cy="50%" fx="10%" fy="50%" r=".5">
            <animate attributeName="fx" dur="23.5s" values="0%;3%;0%" repeatCount="indefinite" />
            <stop offset="0%" stop-color="#000" />
            <stop offset="100%" stop-color="#000" />
          </radialGradient>
          <radialGradient id="Gradient3" cx="50%" cy="50%" fx="50%" fy="50%" r=".5">
            <animate attributeName="fx" dur="21.5s" values="0%;3%;0%" repeatCount="indefinite" />
            <stop offset="10%" stop-color="#fff" />
            <stop offset="50%" stop-color="#000" />
          </radialGradient>
        </defs>
        <rect x="0" y="0" width="100%" height="100%" fill="url(#Gradient1)">
          <animate attributeName="x" dur="20s" values="25%;0%;25%" repeatCount="indefinite" />
          <animate attributeName="y" dur="21s" values="0%;25%;0%" repeatCount="indefinite" />
          <animateTransform attributeName="transform" type="rotate" from="0 50 50" to="360 50 50" dur="17s" repeatCount="indefinite" />
        </rect>
        <rect x="0" y="0" width="100%" height="100%" fill="url(#Gradient2)">
          <animate attributeName="x" dur="23s" values="-25%;0%;-25%" repeatCount="indefinite" />
          <animate attributeName="y" dur="24s" values="0%;50%;0%" repeatCount="indefinite" />
          <animateTransform attributeName="transform" type="rotate" from="0 50 50" to="360 50 50" dur="18s" repeatCount="indefinite" />
        </rect>
        <rect x="0" y="0" width="100%" height="100%" fill="url(#Gradient3)">
          <animate attributeName="x" dur="25s" values="0%;25%;0%" repeatCount="indefinite" />
          <animate attributeName="y" dur="26s" values="0%;25%;0%" repeatCount="indefinite" />
          <animateTransform attributeName="transform" type="rotate" from="360 50 50" to="0 50 50" dur="19s" repeatCount="indefinite" />
        </rect>
      </svg>
      <div className="calculator">
        <div className="calculator-grid">
          <div className="output">
            <input  type="text" value={previousCalculation} 
            onClick={() => setInput(previousCalculation)} />
          <input type="text" value={input || result} onKeyDown={keyPress} />
          </div>
          <button name="CE" onClick={handleButtonPress} className="span-two" 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>CE</p>
          </button>
          <button name="DEL" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>DEL</p>
          </button>
          <button name="/" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>/</p>
          </button>
          <button name="*" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>*</p>
          </button>
          <button name="1" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>1</p>
          </button>
          <button name="2" onClick={handleButtonPress}
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>2</p>
          </button>
          <button name="3" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>3</p>
          </button>
          <button name="4" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>4</p>
          </button>
          <button name="+" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>+</p>
          </button>
          <button name="5" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>5</p>
          </button>
          <button name="6" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>6</p>
          </button>
          <button name="7" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>7</p>
          </button>
          <button name="8" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>8</p>
          </button>
          <button name="-" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>-</p>
          </button>
          <button name="10" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>9</p>
          </button>
          <button name="." onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>.</p>
          </button>
          <button name="0" onClick={handleButtonPress} 
          onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>0</p>
          </button>
          <button name="=" onClick={handleButtonPress} 
          className="span-two" onMouseDown={() => setIsActive(true)} onMouseUp={() => setIsActive(false)}>
            <p className={isActive ? 'active' : ''}>=</p>
          </button>
        </div>
      </div>
    </body>
  );
};

export default Calculator
